import React, { useState } from 'react';
import axios from 'axios';
import { Container, Modal, Row, Col, Card, Button } from "react-bootstrap";
import './Home.css';
import Menu from '../components/Menu';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [{
                title: ""
            }],
            show: false, 
            setShow: false
        }
    }

    componentDidMount() {
        this.getData()
    }

    getData = () => {
        axios.get("http://localhost:8081/admin/products").then((res) => {
            this.setState({ data: res.data.data });
        }).catch((error) => {
            console.log(error);
        });
    }

    handleDelete = (product_id) => {
        console.log(product_id);
        axios.get('http://localhost:8081/admin/delete-products?product_id=' + product_id).then(res => {
            console.log(res.data);
            if (res.data.result) {
                this.getData();
            }
        }).catch(error => {
            console.log(error);
        });
    }

    handleClose = () => this.setState({setShow: false});
    handleShow = () => this.setState({setShow: true});
    

    showModal = (item) => {
        return(
            <Container>
                <Modal show={this.state.show} onHide={this.state.handleClose}>
                    <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                    <Button variant="secondary" onClick={this.state.handleClose}>
                        Close
                    </Button>
                    </Modal.Footer>
                </Modal>
            </Container>
        );
    }

    render() {
        
        return (
            <>
                <this.showModal />
                <Menu />
                <Container className="mt-5">
                    <h1>สินค้าทั้งหมด</h1>
                    <p>จำนวน {this.state.data.length} ชิ้น</p>
                    <Row>
                        { this.state.data.map(item => (
                            <Col md={3}>
                                <Card className="mb-3" style={{ height: '100%', boxShadow: "2px 2px 1px #CFCFCF" }} onClick={this.handleShow} >
                                    <div style={{height: '100%', overflow: 'hidden'}}>
                                        <Card.Img variant="top" className="product_img" src={item.p_img} style={{ width: '100%'}} />
                                    </div>
                                    <Card.Body>
                                        <Card.Title>{item.p_name}</Card.Title>
                                        <Card.Text style={{color: "#A3A3A3"}}>
                                            <p>Brand {item.p_brand_id}</p>
                                        </Card.Text>
                                        <p className="price">฿ {(Number(item.p_price).toLocaleString())}</p>
                                    </Card.Body>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                </Container>
            </>
            
        )
    }
}

export default Home;
