import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import { Container, Table } from 'react-bootstrap';

class Admin extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [{
                title: ""
            }]
        }
    }

    componentDidMount() {
        this.getData()
    }

    getData = () => {
        axios.get("http://localhost:8081/admin/products").then((res) => {
            this.setState({ data: res.data.data });
        }).catch((error) => {
            console.log(error);
        });
    }

    handleDelete = (p_id) => {
        console.log(p_id);
        axios.get('http://localhost:8081/admin/delete-products?p_id=' + p_id).then(res => {
            console.log(res.data);
            if (res.data.result) {
                this.getData();
            }
        }).catch(error => {
            console.log(error);
        });
    }
    render() {
        return (
            <Container>
                <h1>รายการสินค้า</h1>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>ID</th>
                        <th>ชื่อสินค้า</th>
                        <th>รูปภาพ</th>
                        <th>Username</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                        <td>1</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                        </tr>
                        <tr>
                        <td>2</td>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                        </tr>
                        <tr>
                        <td>3</td>
                        <td colSpan={2}>Larry the Bird</td>
                        <td>@twitter</td>
                        </tr>
                    </tbody>
                </Table>
            </Container>
        )
    }
}

export default Admin;
