import axios from 'axios';
import React from 'react';
import { Redirect } from 'react-router';
import { Container, Form } from 'react-bootstrap';

class CreateProducts extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      p_name: '',
      p_descrip: '',
      p_qty:'',
      p_price: 0,
      p_img:'',
      redirect: null
    }
  }

  handleChange = (e) => {
    console.log(e.target.name, e.target.value);
    let name = e.target.name;
    let value = e.target.value;
    this.setState({
      [name]: value
    });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:8081/admin/add-products', this.state).then(res => {
      console.log(res.data);
      if (res.data.result) {
        this.setState({ redirect: '/admin' });
      }
    }).catch(error => {
      console.log(error);
    });
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }
    return (
        <Container>
            <Form onSubmit={this.handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Product Brand</Form.Label>
                    <Form.Control type="text" placeholder="แบรนด์" value={this.state.p_brand_id} name="p_brand" onChange={this.handleChange}/>
                </Form.Group>
            </Form>
        </Container>
    )
  }
}

export default CreateProducts;
