import React from 'react';
import axios from 'axios';
class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [{
                title: ""
            }]
        }
    }

    componentDidMount() {
        this.getData()
    }

    getData = () => {
        axios.get("http://localhost:8081/admin/products").then((res) => {
            this.setState({ data: res.data.data });
        }).catch((error) => {
            console.log(error);
        });
    }

    handleDelete = (product_id) => {
        console.log(product_id);
        axios.get('http://localhost:8081/admin/delete-products?product_id=' + product_id).then(res => {
            console.log(res.data);
            if (res.data.result) {
                this.getData();
            }
        }).catch(error => {
            console.log(error);
        });
    }
    render() {
        return (
            <div>
                <div className="brand">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12">
                                <div className="titlepage">
                                    <h2>Our Car</h2>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="container py-5">
                    <div className="row">
                        <div className="col-lg-12">
                            <div className="row">
                            </div>
                            <div className="row">
                                {this.state.data.map(item => (
                                    <div className="col-md-4">
                                        <div className="card mb-4 product-wap rounded-0">
                                            <div className="card rounded-0">
                                                <img className="card-img rounded-0 img-fluid" src={item.p_img} />
                                                <div className="card-img-overlay rounded-0 product-overlay d-flex align-items-center justify-content-center">
                                                    <ul className="list-unstyled">
                                                        <li><a className="btn btn-success text-white" href="shop-single.html"><i className="assets/webfonts/far fa-heart"></i></a></li>
                                                        <li><a className="btn btn-success text-white mt-2" href="shop-single.html"><i className="assets/webfonts/far fa-eye"></i></a></li>
                                                        <li><a className="btn btn-success text-white mt-2" href="shop-single.html"><i className="assets/webfonts/fas fa-cart-plus"></i></a></li>
                                                    </ul>
                                                </div>
                                            </div>
                                            <div className="card-body">
                                                <a href="shop-single.html" className="h3 text-decoration-none">{item.p_name}</a>
                                                <ul className="w-100 list-unstyled d-flex justify-content-between mb-0">
                                                    <li>{item.p_descrip}</li>
                                                    <li className="pt-2">
                                                        <span className="product-color-dot color-dot-red float-left rounded-circle ml-1"></span>
                                                        <span className="product-color-dot color-dot-blue float-left rounded-circle ml-1"></span>
                                                        <span className="product-color-dot color-dot-black float-left rounded-circle ml-1"></span>
                                                        <span className="product-color-dot color-dot-light float-left rounded-circle ml-1"></span>
                                                        <span className="product-color-dot color-dot-green float-left rounded-circle ml-1"></span>
                                                    </li>
                                                </ul>
                                                <ul className="list-unstyled d-flex justify-content-center mb-1">
                                                    <li>
                                                        <i className="text-warning fa fa-star"></i>
                                                        <i className="text-warning fa fa-star"></i>
                                                        <i className="text-warning fa fa-star"></i>
                                                        <i className="text-muted fa fa-star"></i>
                                                        <i className="text-muted fa fa-star"></i>
                                                    </li>
                                                </ul>
                                                <p className="text-center mb-0">฿{item.p_price}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>

                            <div div="row">
                                <ul className="pagination pagination-lg justify-content-end">
                                    <li className="page-item disabled">
                                        <a className="page-link active rounded-0 mr-3 shadow-sm border-top-0 border-left-0" href="#" tabindex="-1">1</a>
                                    </li>
                                    <li className="page-item">
                                        <a className="page-link rounded-0 mr-3 shadow-sm border-top-0 border-left-0 text-dark" href="#">2</a>
                                    </li>
                                    <li className="page-item">
                                        <a className="page-link rounded-0 shadow-sm border-top-0 border-left-0 text-dark" href="#">3</a>
                                    </li>
                                </ul>
                            </div>
                        </div>

                    </div>
                </div>
            </div>

        )
    }
}

export default Home;
