import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

class Admin extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [{
                title: ""
            }]
        }
    }

    componentDidMount() {
        this.getData()
    }

    getData = () => {
        axios.get("http://localhost:8081/admin/products").then((res) => {
            this.setState({ data: res.data.data });
        }).catch((error) => {
            console.log(error);
        });
    }

    handleDelete = (product_id) => {
        console.log(product_id);
        axios.get('http://localhost:8081/admin/delete-products?product_id=' + product_id).then(res => {
            console.log(res.data);
            if (res.data.result) {
                this.getData();
            }
        }).catch(error => {
            console.log(error);
        });
    }
    render() {
        return (
            <div className="container py-5">
                <main>
                    <div className="row">
                        <div className="col-md-12">
                            <Link to="/create-products" className="btn btn-info">create</Link>
                            <table className="table">
                                <tr>
                                    <th>ชื่อสินค้า</th>
                                    <th>ภาพประกอบ</th>
                                    <th>ราคา</th>
                                    <th>action</th>
                                </tr>
                                {this.state.data.map(item => (
                                    <tr>
                                        <td>{item.title}</td>
                                        <td>{item.image_url}</td>
                                        <td>{item.price}</td>
                                        <td>
                                            <Link to={"/update-products/" + item.product_id} className="btn btn-info">Update</Link>
                                            <button className="btn btn-danger" onClick={() => { if (window.confirm('Are you sure you wish to delete this item?')) this.handleDelete(item.product_id) }}>Delete</button>
                                        </td>
                                    </tr>
                                ))}
                            </table>
                        </div>
                    </div>
                </main>
            </div>
        )
    }
}

export default Admin;
