import axios from 'axios';
import React from 'react';
import { Redirect } from 'react-router';

class CreateProducts extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      p_name: '',
      p_descrip: '',
      p_qty:'',
      p_price: 0,
      p_img:'',
      p_type:'',
      redirect: null
    }
  }

  handleChange = (e) => {
    console.log(e.target.name, e.target.value);
    let name = e.target.name;
    let value = e.target.value;
    this.setState({
      [name]: value
    });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:8081/admin/add-products', this.state).then(res => {
      console.log(res.data);
      if (res.data.result) {
        this.setState({ redirect: '/' });
      }
    }).catch(error => {
      console.log(error);
    });
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.redirect} />
    }
    return (
      <main className="container py-5">
        <div className="row">
          <div className="col-md-6">
            <form onSubmit={this.handleSubmit}>
              <div class="form-group">
                <label>ชื่อสินค้า</label>
                <input type="text" name="title" class="form-control" placeholder="ชื่อสินค้า" onChange={this.handleChange} />
              </div>
              <div class="form-group">
                <label>รายละเอียด</label>
                <input type="text" name="description" class="form-control" placeholder="รายละเอียด" onChange={this.handleChange} />
              </div>
              <div class="form-group">
                <label>จำนวน</label>
                <input type="text" name="price" class="form-control" placeholder="จำนวน" onChange={this.handleChange} />
              </div>
              <div class="form-group">
                <label>ที่อยู่รูปภาพ</label>
                <input type="text" name="image_url" class="form-control" placeholder="ที่อยู่รูปภาพ" onChange={this.handleChange} />
              </div>
              <div class="form-group">
                <label>ราคา</label>
                <input type="text" name="price" class="form-control" placeholder="ราคา" onChange={this.handleChange} />
              </div>
              <button type="submit" class="btn btn-primary">บันทึก</button>
            </form>
            <br />
          </div>
        </div>
      </main>
    )
  }
}

export default CreateProducts;
