import axios from 'axios';
import React from 'react';
import { Container, Form, Button, Row, Col } from 'react-bootstrap';
import { Redirect } from 'react-router';

class UpdateProducts extends React.Component {
    constructor(props){
      super(props);
      this.state = {
        p_id: props.match.params.p_id,
        p_name: '',
        p_descrip:'',
        p_qty:'',
        p_img: '',
        p_price: 0,
        p_color: '', 
        p_brand_id: '',
        redirect: null
      }
    }

    componentDidMount(){
      axios.get('http://localhost:8081/admin/edit-products/'+this.state.p_id).then(res => {
        let data = res.data.data[0];
        console.log(data);
        this.setState({
          p_name: data.p_name,
          p_descrip: data.p_descrip,
          p_qty: data.p_qty,
          p_img: data.p_img,
          p_price: data.p_price,
          p_color: data.p_color, 
          p_brand_id: data.p_brand_id,
        });
      }).catch(error => {
        console.log(error);
      });
    }

    handleChange = (e) => {
      console.log(e.target.name, e.target.value);
      let name = e.target.name;
      let value = e.target.value;
      this.setState({
        [name]: value
      });
    }

    handleSubmit = (e) => {
      e.preventDefault();
      axios.post('http://localhost:8081/admin/edit-products', this.state).then(res => {
        console.log(res.data);
        if(res.data.result){
          this.setState({redirect: '/admin'});
        }
      }).catch(error => {
        console.log(error);
      });
    }
    
    render(){
        if (this.state.redirect) {
          return <Redirect to={this.state.redirect} />
        }
        return(
            <Container className="mt-5">
                <h1>แก้ไขสินค้า</h1>
                <Form>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Product Name</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label>Product Description</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                    </Form.Group>
                    <Row>
                        <Col>
                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Product Quantity</Form.Label>
                                <Form.Control type="password" placeholder="Password" />
                            </Form.Group>
                        </Col>
                        <Col>
                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Product Quantity</Form.Label>
                                <Form.Control type="password" placeholder="Password" />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>
            </Container>
        )
    }
}

export default UpdateProducts;
