import React, { useState } from 'react';
import axios from 'axios';
import { Container, Modal, Row, Col, Card, Button } from "react-bootstrap";
import './Home.css';
import Menu from '../components/Menu';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [{
                title: ""
            }],
            product: {},
            show: false, 
            setShow: false
        }
        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    componentDidMount() {
        this.getData()
    }

    getData = () => {
        axios.get("http://localhost:8081/admin/products").then((res) => {
            this.setState({ data: res.data.data });
        }).catch((error) => {
            console.log(error);
        });
    }

    handleDelete = (product_id) => {
        console.log(product_id);
        axios.get('http://localhost:8081/admin/delete-products?product_id=' + product_id).then(res => {
            console.log(res.data);
            if (res.data.result) {
                this.getData();
            }
        }).catch(error => {
            console.log(error);
        });
    }

    handleClose = () => {
        this.setState({show: false});
    }
    
    handleShow = () => {
        this.setState({show: true});
    };
    

    showModal = () => {
        return(
            <Container>
                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                    <Modal.Title>{this.state.product.p_name}</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Row>
                            <Col>
                                <img src={this.state.product.p_img} />
                            </Col>
                            <Col style={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                                <p>{this.state.product.p_descrip}</p>
                                <p><span>จำนวนสินค้าคง</span> {this.state.product.p_qty}</p>
                                <p><span>สี</span> {this.state.product.p_color}</p>
                                <p><span>แบรนด์</span> {this.state.product.p_qty}</p>
                                <p><span>ราคา</span> {this.state.product.p_qty}</p>
                            </Col>
                        </Row>
                    </Modal.Body>
                    <Modal.Footer>
                    <Button variant="secondary" onClick={this.handleClose}>
                        Close
                    </Button>
                    </Modal.Footer>
                </Modal>
            </Container>
        );
        
    }

    render() {
        
        return (
            <>
                <Menu />
                <this.showModal />
                <Container className="mt-5">
                    <h1>สินค้าทั้งหมด</h1>
                    <p>จำนวน {this.state.data.length} ชิ้น</p>
                    <Row>
                        
                        { this.state.data.map(item => (
                            <Col md={3}>
                                <Card className="mb-3" style={{ height: '100%', boxShadow: "2px 2px 1px #CFCFCF", cursor: "pointer" }} onClick={() => { this.setState({product: item}); this.handleShow();}} >
                                    <div style={{height: '100%', overflow: 'hidden'}}>
                                        <Card.Img variant="top" className="product_img" src={item.p_img} style={{ width: '100%'}} />
                                    </div>
                                    <Card.Body>
                                        <Card.Title>{item.p_name}</Card.Title>
                                        <Card.Text style={{color: "#A3A3A3"}}>
                                            <p>Brand {item.p_brand_id}</p>
                                        </Card.Text>
                                        <p className="price">฿ {(Number(item.p_price).toLocaleString())}</p>
                                    </Card.Body>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                </Container>
            </>
            
        )
    }
}

export default Home;
